import './style.css'
import { useState, useRef } from 'react'
import EyeOpenIcon from './../../assets/open-eye.svg'
import EyeCloseIcon from './../../assets/close-eye.svg'

function Main() {
  const [email, SetEmail] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [eyesOpen, setEyesOpen] = useState(false)
  const inputPasswordRef = useRef(null)

  function handleSubmit(event) {
    event.preventDefault()
    setError('')

    if (!email) {
      setError('Por favor preencha o campo e-mail corretamente')
      return
    }
    if (password.length < 8) {
      setError('A senha precisa ter no mínimo 8 caracteres')
      return
    }
    setSuccess('Login efetuado com sucesso')
  }

  function handlePassword() {
    setEyesOpen(!eyesOpen)
    if (eyesOpen) {
      inputPasswordRef.current.type = 'text'
    } else {
      inputPasswordRef.current.type = 'password'
    }
  }

  return (
    <div className="container-main">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="E-mail"
          value={email}
          onChange={(event) => SetEmail(event.target.value)}
        />

        <input
          ref={inputPasswordRef}
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />

        <img
          className="eyes"
          src={eyesOpen ? EyeOpenIcon : EyeCloseIcon}
          alt={eyesOpen ? 'Mostrar senha' : 'Ocultar senha'}
          onClick={handlePassword}
        />

        {error !== '' ? <span className="error">{error}</span> : ''}

        {success !== '' ? <span className="success">{success}</span> : ''}

        <button type="submit">Login</button>
      </form>
    </div>
  )
}

export default Main
