import './style.css'
import { useState } from 'react'
import EyeOpenIcon from './../../assets/open-eye.svg'
import EyeCloseIcon from './../../assets/close-eye.svg'

function Main() {
  const [form, setForm] = useState({
    email: '',
    password: '',
  })
  const [email, SetEmail] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')
  const [success, setSuccess] = useState('')
  const [eyesOpen, setEyesOpen] = useState(false)

  function handleSubmit(event) {
    event.preventDefault()
    setError('')

    if (!form.email) {
      setError('Por favor preencha o campo e-mail corretamente')
      return
    }
    if (password.length < 8) {
      setError('A senha precisa ter no mínimo 8 caracteres')
      return
    }
    setSuccess('Login efetuado com sucesso')
  }

  function handlePassword() {
    setEyesOpen(!eyesOpen)
  }

  return (
    <section className="container-main">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="E-mail"
          value={email}
          onChange={(event) => setForm({ ...form, email: event.target.value })}
        />

        <div className="container-input-password">
          <input
            type={eyesOpen ? 'text' : 'password'}
            placeholder="Senha"
            value={password}
            onChange={(event) =>
              setForm({ ...form, password: event.target.value })
            }
          />

          <img
            className="eyes"
            src={eyesOpen ? EyeOpenIcon : EyeCloseIcon}
            alt={eyesOpen ? 'Mostrar senha' : 'Ocultar senha'}
            onClick={handlePassword}
          />
        </div>

        {error !== '' ? <span className="error">{error}</span> : ''}

        {success !== '' ? <span className="success">{success}</span> : ''}

        <button type="submit">Login</button>
      </form>
    </section>
  )
}

export default Main
