import './style.css';
import { useState } from 'react';
import EyeOpen from './../../assets/open-eye.svg';
import EyeClose from './../../assets/close-eye.svg';

function Main() {

  const [email, SetEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [byPassword, setByPassword] = useState('');

  function handleSubmit(event) {
    event.preventDefault();
    setError('')

    if(!email) {
      setError('Por favor preencha o campo e-mail corretamente')
      return
    } 
    if(password.length <8){
      setError('A senha precisa ter no mínimo 8 caracteres');
      return
    }
    setSuccess('Login efetuado com sucesso')
   }

   function handlePassword() {
      setByPassword(!byPassword)
   }

  return (
    <div className='container-main'>
      <form onSubmit={handleSubmit}>

        <input type="text" placeholder='E-mail' value={email} onChange={(event) => SetEmail(event.target.value)}/>

        <input type='password' placeholder='Senha' value={password} onChange={(event) => setPassword(event.target.value)}/>

        <img className='eyes' src={byPassword ? EyeOpen : EyeClose} alt={byPassword ? 'Mostrar senha' : 'Ocultar senha'} onClick={handlePassword}/>

        { error !== "" ? <span className='error'>{error}</span> : ''}

        { success !== ''? <span className='success'>{success}</span> : ''}

        <button type='submit'>Login</button>
    </form>
    </div>
  );
}

export default Main;
